function setup() {
	createCanvas(windowWidth-2*margin, windowHeight-2*margin,WEBGL);
  setAttributes('antialias', true);
	setAttributes('perPixelLighting', true);
	camera(-440,-160,1100,-100,0,0,0,1,0);
	//colorMode(HSB,360,100,100,100);
	colorMode(RGB,255,255,255,100);

	//textSize(10);
	/*fontRegular = loadFont('assets/OpenSans-Regular.ttf');
 	textFont(fontRegular);*/

  //strokeWeight(1);
  //background(100);

  //noStroke();
  //stroke('grey');

	sel = createSelect();
  sel.position(10+margin, 180+margin);
  sel.option(4);
  sel.option(8);
  sel.option(16);
  sel.option(32);
	sel.option(64);
	sel.selected(4);
  //sel.changed();
	//https://p5js.org/reference/#/p5/createSelect

	slider1x = createSlider(0, 1, 1, 0.05);
	slider1x.position(10+margin, 30+margin);
	slider1x.style('width', '80px');

	slider1y = createSlider(0, 1, 1, 0.05);
	slider1y.position(10+margin, 60+margin);
	slider1y.style('width', '80px');

	slider1z = createSlider(0, 1, 1, 0.05);
	slider1z.position(10+margin, 90+margin);
	slider1z.style('width', '80px');

	slider2 = createSlider(-2,2,0,0.1);
	slider2.position(10+margin, 120+margin);
	slider2.style('width', '80px');

	orbitControlCheck = createCheckbox(
       "Orbit Control", true);

  orbitControlCheck.position(10+margin, 150+margin);

	// Toggle default light
  orbitControlCheck.changed(() => {
    orbitControlEnable = !orbitControlEnable;
		//noLoop();
  });
	/*
	camx = createSlider(-1000, +1000, 0, 10);
	camx.position(10+margin, 210+margin);
	camx.style('width', '80px');

	camy = createSlider(-1000, +1000, 0, 10);
	camy.position(10+margin, 240+margin);
	camy.style('width', '80px');

	camz = createSlider(0, +2000, 0, 10);
	camz.position(10+margin, 270+margin);
	camz.style('width', '80px');
	*/

}

function draw() {

	n = sel.value();

	/*
	X = camx.value();
	Y = camy.value();
	Z = camz.value();
	camera(X, Y, Z, 0,0,0, 0, 1, 0);
	print(X,Y,Z);
	*/

	if (orbitControlEnable) {
    // Enable orbit control
		orbitControl([1], [1], [1]);
		speed = slider2.value();
		//rotateZ(frameCount * 0.01*speed);
		//rotateX(frameCount * 0.01*speed);
		rotateY(frameCount * 0.01*speed);
  }
	/**/

	size = 50*16/n;
	//size = (windowWidth+windowHeight)/2/(n^2);//windowWidth/n; //(n^2); //640
	translate(-0.5*n*size,-0.5*n*size,-0.5*n*size);

	background(220);
	pointLight(255,255,255,-200,-200,200);
	ambientLight(0, 255, 255);

	hx = slider1x.value();
	hy = slider1y.value();
	hz = slider1z.value();
	sponge(n,hx,hy,hz);

}

function sponge(n,hx,hy,hz) {
	normalMaterial();
	fill(250, 100, 100);
	size1 = 50*16/n;

	if (n == 4) {
		A = A4;
	}	else if (n == 8) {
		A = A8;
	} else if (n == 16) {
		A = A16;
	} else if (n == 32) {
		A = A32;
	} else if (n == 64) {
		A = A64;
	}

	for (var i = 0; i < n; i++) {
		for (var j = 0; j < n; j++) {
			push();
			a = i+j*n;
			translate(i*size1*hx,A[a]*size1*hy, j*size1*hz);
			strokeWeight(0.2);
			stroke(51);
			box(size1);
			pop();
		}
	}

}

