var gui;

function setup() {

	createCanvas(windowWidth-2*margin, windowHeight-2*margin,WEBGL);
  setAttributes('antialias', true);
	setAttributes('perPixelLighting', true);
	camera(-440,-160,1100,-100,0,0,0,1,0);
	colorMode(RGB,255,255,255,100);

	gui = createGui('Regler');
	gui.setPosition(width - 220, 40);
	gui.addGlobals('slider1x','slider1y','slider1z','speed','power','orbControl');

}

function draw() {

	n = pow(2,power);

	/*if (orbitControlEnable) {
    // Enable orbit control
		orbitControl([1], [1], [1]);
		//rotateZ(frameCount * 0.01*speed);
		//rotateX(frameCount * 0.01*speed);
		rotateY(frameCount * 0.01*speed);
  }*/

	if (orbControl == 1) {
    // Enable orbit control
		orbitControl([1], [1], [1]);
		//rotateZ(frameCount * 0.01*speed);
		//rotateX(frameCount * 0.01*speed);
		rotateY(frameCount * 0.01*speed);
  } else if (orbControl == 0) {

  }
	/**/

	size = 50*16/n;

	translate(-0.5*n*size,-0.5*n*size,-0.5*n*size);

	background(220); //ohne diese Zeile bleiben die alten Positionen angezeigt. alternativ: clear();

	directionalLight(0,255,255,-100,+100,-100);
	directionalLight(255,0,0,0,100,0);
	directionalLight(0,200,100,0,-200,+100);
	directionalLight(0,100,90,0,-200,-100);
	ambientLight(255, 80, 80,30);

	hx = slider1x;
	hy = slider1y;
	hz = slider1z;
	sponge(n,hx,hy,hz);

}

function sponge(n,hx,hy,hz) {
	normalMaterial();
	fill(100, 200, 255,100);
	size1 = 50*16/n;

	if (n == 2) {
		A = A2;
	} else if (n == 4) {
		A = A4;
	} else if (n == 8) {
		A = A8;
	} else if (n == 16) {
		A = A16;
	} else if (n == 32) {
		A = A32;
	} else if (n == 64) {
		A = A64;
	}

	for (var i = 0; i < n; i++) {
		for (var j = 0; j < n; j++) {
			push();
			a = i+j*n;
			translate(i*size1*hx,A[a]*size1*hy, j*size1*hz);
			strokeWeight(0.2);
			stroke(51);
			box(size1);
			pop();
		}
	}

}

